CREATE DATABASE Blog;

USE Blog;

CREATE TABLE Posts (
    id INT PRIMARY KEY AUTO_INCREMENT,
    title TEXT,
    author TEXT,
    content TEXT,
    image TEXT

);

CREATE TABLE Comments (
    id INT AUTO_INCREMENT,
    username TEXT,
    text TEXT,
    image TEXT,
    post_id INT,
    PRIMARY KEY(id),
    FOREIGN KEY(post_id) REFERENCES Posts(id)
    ON DELETE CASCADE

);




